package com.sun;

/**与えられた二つの整数 a と b の和、差、積、商（整数除算）、余りを計算するJavaプログラムを作成してください。


 * 
 */


public class Java5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 15;
		int b = 4;
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
	}

}
